import { useState, useEffect } from 'react';
import { scrollToElement } from '../utils/scrollUtils';

const Hero = () => {
  // ... (keep your existing typing animation logic here) ...

  // Create an array for 20 meteors
  const meteors = new Array(20).fill(true);

  return (
    <section id="home" className="relative min-h-screen flex items-center justify-center bg-slate-950 overflow-hidden">
      
      {/* Meteor Background Layer */}
      <div className="absolute inset-0 z-0 pointer-events-none">
        {meteors.map((_, idx) => (
          <span
            key={"meteor" + idx}
            className="animate-meteor"
            style={{
              top: Math.floor(Math.random() * 100) + "%",
              left: Math.floor(Math.random() * 100) + "%",
              animationDelay: Math.random() * (0.8 - 0.2) + 0.2 + "s",
              animationDuration: Math.floor(Math.random() * (10 - 2) + 2) + "s",
            }}
          ></span>
        ))}
      </div>

      {/* Main Content Layer */}
      <div className="relative z-10 text-center px-4">
        <h1 className="text-5xl md:text-7xl font-bold text-white mb-6">
          Hi, I'm <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-indigo-500">
            [Your Name]
          </span>
        </h1>
        
        <p className="text-xl md:text-2xl text-slate-300 max-w-2xl mx-auto mb-10">
          {displayText}
          <span className="animate-pulse text-blue-400">|</span>
        </p>
        
        <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
          <button 
            onClick={() => scrollToElement('projects')}
            className="btn-primary border-none bg-blue-600 hover:bg-blue-500"
          >
            View My Work
          </button>
          <button 
            onClick={() => scrollToElement('contact')}
            className="px-6 py-3 rounded-lg border border-slate-700 text-white hover:bg-slate-800 transition-all"
          >
            Get In Touch
          </button>
        </div>
      </div>

      {/* Animated Scroll Down Indicator */}
      <div className="absolute bottom-10 left-1/2 -translate-x-1/2 animate-bounce">
        <svg className="w-6 h-6 text-slate-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7" />
        </svg>
      </div>
    </section>
  );
};

export default Hero;
